public static class Program
{
    public static void Solve()
    {
        var n = I;
        var d = Repeat(n, () => I).ToArray();

        if (n == 1) { Console.WriteLine(0); return; }

        var list = new List<Pair<int, int>>();

        for (var i = 0; i < n - 1; i++)
            for (var j = i + 1; j < n; j++)
            {
                var t1 = Abs(d[i] - d[j]);
                var t2 = Abs(d[i] - (24 - d[j]));
                if (t1 > t2) Swap(ref t1, ref t2);
                list.Add(Pair.Create(t1, t2));
            }

        var c1 = Comb(n, 2);
        Assert(list.Count == c1);

        var ans = 0;

        Func<int, int> TryInner = c2 =>
        {
            Assert(c2 > 0 || c1 - c2 > 0);
            var t1 = c2 > 0 ? list.Take(c2).Min(x => x.First) : int.MaxValue;
            var t2 = c1 - c2 > 0 ? list.AsEnumerable().Reverse().Take(c1 - c2).Min(x => x.Second) : int.MaxValue;
            return Min(t1, t2);
        };

        Action Try = () =>
        {
            var m1 = n;
            var m2 = 0;

            while (0 <= m1 && m1 <= n && 0 <= m2 && m2 <= n)
            {
                var c2 = Comb(m1, 2) + Comb(m2, 2);
                var t = TryInner(c2);
                ans = Max(ans, t);
                m1--; m2++;
            }
        };

        list.Sort((x, y) => y.CompareTo(x));
        Try();
        list = list.Select(p => Pair.Create(p.Second, p.First)).ToList();
        list.Sort((x, y) => y.CompareTo(x));
        Try();

        Assert(ans != int.MaxValue);
        Console.WriteLine(ans);
    }

