    {
        var sw = new StreamWriter(Console.OpenStandardOutput());
        sw.NewLine = "\n";
#if DEBUG
        sw.AutoFlush = true;
        //Console.SetError(sw);
#else
        sw.AutoFlush = false;
#endif
