namespace AtCoder
{
    class Program
    {
        private static string Read() { return Console.ReadLine(); }
        private static int ReadInt() { return int.Parse(Read()); }
        private static long ReadLong() { return long.Parse(Read()); }
        private static double ReadDouble() { return double.Parse(Read()); }
        private static int[] ReadInts() { return Array.ConvertAll(Read().Split(), int.Parse); }
        private static long[] ReadLongs() { return Array.ConvertAll(Read().Split(), long.Parse); }
        private static double[] ReadDoubles() { return Array.ConvertAll(Read().Split(), double.Parse); }

        private static int Calculate(int[] array)
        {
            int N = array.Length;
            int min = int.MaxValue;
            for (int i = 0; i < N; ++i)
            {
                int t1 = array[i];
                for (int j = i + 1; j < N; ++j)
                {
                    int t2 = array[j];
                    int t = Math.Abs(t2 - t1);
                    min = Math.Min(Math.Min(t, 24 - t), min);
                }
            }
            return min;
        }

        private static int Max(int[] array1, int[] array2)
        {
            int N = array2.Length;
            int max = 0;
            for (int i = 0; i < (1 << N); ++i)
            {
                var array3 = new int[N];
                for (int j = 0; j < N; ++j)
                {
                    array3[j] = (i & (1 << j)) == 0 ? array2[j] : 24 - array2[j];
                }
                max = Math.Max(Calculate(Enumerable.Union(array1, array3).ToArray()), max);
            }
            return max;
        }

        private static int Solve(int N, int[] D)
        {
            int[] counts = new int[13];
            foreach (int d in D) { ++counts[d]; }
            if (counts[0] > 0) { return 0; }
            var list1 = new List<int> { 0 };
            var list2 = new List<int>();
            for (int i = 0; i <= 12; ++i)
            {
                switch (counts[i])
                {
                case 0:
                    break;
                case 1:
                    if (i < 12)
                    {
                        list2.Add(i);
                    }
                    else
                    {
                        list1.Add(i);
                    }
                    break;
                case 2:
                    if (i < 12)
                    {
                        list1.Add(i);
                        list1.Add(24 - i);
                    }
                    else
                    {
                        return 0;
                    }
                    break;
                default:
                    return 0;
                }
            }
            return Max(list1.ToArray(), list2.ToArray());
        }

        static void Main(string[] args)
        {
            int N = ReadInt();
            int[] D = ReadInts();
            Console.WriteLine(Solve(N, D));
        }
    }
}

