                    on[24 - i] = true;
                }
                if (cnts[i] == 1)
                {
                    vals.Add(i);
                }
            }
            int allPat = 1 << vals.Count;
            int res = 0;
            for(int i = 0; i < allPat; i++)
            {
                bool[] onTemp = new bool[25];
                for(int j = 0; j <= 24; j++)
                {
                    onTemp[j] = on[j];
                }
                for(int j = 0; j < vals.Count; j++)
                {
                    if ((i & (1 << j)) > 0)
                    {
                        onTemp[vals[j]] = true;
                    }
                    else
                    {
                        onTemp[24 - vals[j]] = true;
                    }
                }
                int tmp = 0;
                int min = 24;
                for (int j = 1; j <= 24; j++)
                {
                    if (onTemp[j])
                    {
                        min = Min(min, j - tmp);
                        tmp = j;
                    }
                }
                res = Max(res, min);
            }
            WriteLine(res);
        }

