			for(int j=1;j<12;j++){
				if(cnt[j] == 2){
					choice[j] = true;
					choice[24 - j] = true;
				}
				if(cnt[j] == 1){
					if(((t >> j) & 1) == 1){
						choice[j] = true;
					} else {
						choice[24 - j] = true;
					}
				}
			}

			if(cnt[12] == 1) choice[12] = true;


			int last = 0;
			int min = 24;
			for(int j=1;j<=24;j++){
				if(!choice[j % 24]) continue;
				int dist = j - last;
				min = Math.Min(min, dist);
				last = j;
			}

			max = Math.Max(max, min);

		}

		Console.WriteLine(max);
	}
