public static class Program
{
    public static void Solve()
    {
        var n = I;
        var d = Repeat(n, () => I).ToArray();

        if (n == 1) { Console.WriteLine(0); return; }

        var list = new List<Pair<int, int>>();

        for (var i = 0; i < n - 1; i++)
            for (var j = i + 1; j < n; j++)
            {
                var t1 = Abs(d[i] - d[j]);
                var t2 = Abs(d[i] - (24 - d[j]));
                if (t1 > t2) Swap(ref t1, ref t2);
                list.Add(Pair.Create(t1, t2));
            }

        var c1 = Comb(n, 2);
        Assert(list.Count == c1);

        var ans = 0;

        Func<int, int> TryInner = c2 =>
        {
            Assert(c1 - c2 > 0);
            var t1 = c2 > 0 ? list.Take(c2).Min(x => x.First) : int.MaxValue;
            var t2 = list.AsEnumerable().Reverse().Take(c1 - c2).Min(x => x.Second);
            return Min(t1, t2);
        };

        Action Try = () =>
        {
            var m1 = n / 2;
            var m2 = n / 2 + (n % 2 == 0 ? 0 : 1);

            while (m1 > 0 && m2 > 0)
            {
                Assert(m1 <= c1 && m2 <= c1);
                var c2 = Comb(m1, 2) + Comb(m2, 2);
                var t = TryInner(c2);
                ans = Max(ans, t);
                m1--; m2++;
            }
        };

        list.Sort((x, y) => y.CompareTo(x));
        Try();
        list = list.Select(p => Pair.Create(p.Second, p.First)).ToList();
        list.Sort((x, y) => y.CompareTo(x));
        Try();

        Console.WriteLine(ans);
    }

    #region Scanners
    static Scanner _scanner;
    static char C => _scanner.NextChar();
    static string S => _scanner.NextString();
    static int I => _scanner.NextInt();
    static long L => _scanner.NextLong();
    static BigInteger B => _scanner.NextBigInteger();
    static double D => _scanner.NextDouble();
    static decimal M => _scanner.NextDecimal();
    #endregion

    public static void Main()
    {
        var sw = new StreamWriter(Console.OpenStandardOutput());
        sw.NewLine = "\n";
#if DEBUG
        sw.AutoFlush = true;
        //Console.SetError(sw);
#else
        sw.AutoFlush = false;
#endif
        Console.SetOut(sw);
        _scanner = new Scanner(Console.OpenStandardInput());
        Solve();
        Console.Out.Flush();
    }
}

public struct Pair<T1, T2> : IComparable<Pair<T1, T2>>
    where T1 : IComparable<T1>
    where T2 : IComparable<T2>
{
    public T1 First;
    public T2 Second;

    public Pair(T1 first, T2 second)
    {
        First = first;
        Second = second;
    }

    public int CompareTo(Pair<T1, T2> other)
    {
        var t = First.CompareTo(other.First);
        if (t == 0) t = Second.CompareTo(other.Second);
        return t;
    }
}

