                time.Add(12);
            }
            for(int j=0;j<one.Count;j++){
                if(((1<<j)&b) == 0){
                    time.Add(one[j]);
                }
                else{
                    time.Add(24-one[j]);
                }
            }
            for(int j=0;j<two.Count;j++){
