using System;
using System.Collections.Generic;
using System.Linq;
using static System.Console;
using static System.Convert;
using static System.Math;
//using Debug;
//using static System.Globalization.CultureInfo;
using System.Text;
class Program
{
    static void Main(string[] args)
    {
        //Solve();
        WriteLine(Solve());
    }
    static bool[] b;
    static int[] key;
    static int res;
    static int Solve()
    {
        var num = Input.num;
        var ds = Input.ar;
        var dic = new Dictionary<int, int>();
        for (var i = 0; i < num; i++)
            if (dic.ContainsKey(ds[i]))
                dic[ds[i]]++;
            else dic[ds[i]] = 1;
        if (dic.Any(v => v.Value > 2 || v.Key == 0)) return 0;
        b = new bool[24];b[0] = true;
        foreach (var d in dic)
        {
            if (d.Key == 12)
            {
                if (d.Value == 1) b[12] = true;
                else return 0;
            }
            else if (d.Value > 1)
                b[d.Key] = b[24 - d.Key] = true;
        }
        key = dic.Where(v => v.Value == 1 && v.Key != 12).Select(v => v.Key).ToArray();
        if (key.Length != 0)
        {
            dfs(0, true);
            dfs(0, false);
        }
        else
        {
            var i = 1;
            var min = int.MaxValue;
            while (i < 24)
            {
                var len = 1;
                while (i < 24 && !b[i])
                {
                    len++;
                    i++;
                }
                min = Min(len, min);
                i++;
            }
            res = Max(res, min);

        }
        return res;
    }
    static void dfs(int depth,bool s)
    {
        b[key[depth]] = s;
        b[key[depth] + 12] = !s;
        if (depth == key.Length - 1)
        {
            var i = 1;
            var min = int.MaxValue;
            while (i < 24)
            {
                var len = 1;
                while (i < 24 && !b[i])
                {
                    len++;
                    i++;
                }
                min = Min(len, min);
                i++;
            }
            res = Max(res, min);
            return;
        }
        dfs(depth + 1, true);
        dfs(depth + 1, false);
        b[key[depth]] = b[key[depth] + 12] = false;
    }
}

