class Solve{
    public Solve(){}
    StringBuilder sb;
    ReadData re;
    public static int Main(){
        new Solve().Run();
        return 0;
    }
    void Run(){
        sb = new StringBuilder();
        re = new ReadData();
        Calc();
        Console.Write(sb.ToString());
    }
    void Calc(){
        int N = re.i();
        bool ZERO;
        bool TWELVE;
        List<int> one = new List<int>();
        List<int> two = new List<int>();
        {
            int[] A = new int[13];
            for(int i=0;i<N;i++){
                A[re.i()]++;
            }
            A[0]++;
            if(A[0] > 1 || A[12] > 1){
                sb.Append("0\n");
                return;
            }
            ZERO = A[0] != 0;
            TWELVE = A[12] != 0;
            for(int i=1;i<12;i++){
                if(A[i] == 1){
                    one.Add(i);
                }
                if(A[i] == 2){
                    two.Add(i);
                }
                if(A[i] > 2){
                    sb.Append("0\n");
                    return;
                }
            }
        }
        int count = 0;
        for(int b=0;b<1<<one.Count;b++){
            List<int> time = new List<int>();
            if(ZERO){
                time.Add(0);
            }
            if(TWELVE){
                time.Add(12);
            }
            for(int j=0;j<one.Count;j++){
                if(((1<<j)&b) == 0){
                    time.Add(one[j]);
                }
                else{
                    time.Add(24-one[j]);
                }
            }
            for(int j=0;j<two.Count;j++){
                time.Add(two[j]);
                time.Add(24-two[j]);
            }
            int min = 10000;
            for(int i=0;i<time.Count;i++){
                for(int j=i+1;j<time.Count;j++){
                    int diff = Math.Max(time[i]-time[j],time[j]-time[i]);
                    diff = Math.Min(diff,24-diff);
                    min = Math.Min(min,diff);
                }
            }
            count = Math.Max(count,min);
        }
        sb.Append(count+"\n");
    }
}
