package main

import (
	"bufio"
	"fmt"
	"os"
	"sort"
	"strconv"
	"strings"
)

var sc = bufio.NewScanner(os.Stdin)

func nextLine() string {
	sc.Scan()
	return sc.Text()
}

func main() {
	var n int
	count := make([]int, 13)
	n, _ = strconv.Atoi(nextLine())
	inputLine := strings.Split(nextLine(), " ")
	for i := 0; i < n; i++ {
		a, _ := strconv.Atoi(inputLine[i])
		count[a]++
	}
	count[0]++
	var use, cand []int
	use = append(use, 0, 24)
	for i, v := range count {
		if (v >= 2 && (i == 0 || i == 12)) || v >= 3 {
			fmt.Println(0)
			return
		}

		if v == 2 {
			use = append(use, i)
			use = append(use, 24-i)

		} else if v > 0 && i != 0 {
			cand = append(cand, i)
		}
	}
	ans := 0

	for i := 0; i < 1<<uint(len(cand)); i++ {
		nowUse := use
		for j, v := range cand {
			if ((i >> uint(j)) & 1) == 1 {
				nowUse = append(nowUse, v)
			} else {
				nowUse = append(nowUse, 24-v)
			}
		}
		sort.Sort(sort.IntSlice(nowUse))
		tempMin := 12
		for i := 0; i < len(nowUse)-1; i++ {
			distance := nowUse[i+1] - nowUse[i]
			if distance < tempMin {
				tempMin = distance
			}
		}
		if tempMin > ans {
			ans = tempMin
		}
	}
	fmt.Println(ans)

}

