from collections import Counter
from itertools import product, combinations


def solve(n, ds):
    if n >= 24:
        return 0
    freq = Counter(ds)
    if 0 in freq or freq[12] >= 2 or max(freq.values()) >= 3:
        return 0
    cmp = set()
    alt = []
    for d, l in freq.items():
        if l == 2 or d == 12:
            cmp.add(d)
            cmp.add(24 - d)
        else:
            alt.append((d, 24 - d))
    tak = min(freq.keys())
    if alt:
        ans = 0
        for prd in product(*alt):
            tmp = min(tak, min((abs(x - y) for x, y in combinations(cmp.union(prd), 2)), default=0))
            ans = max(ans, tmp)
    else:
        ans = min(tak, min((abs(x - y) for x, y in combinations(cmp, 2)), default=0))
    return ans


