        return 0
    if c.most_common(1)[0][1] >= 3:
        return 0
    l1 = []
    l2 = []
    for i, cnt in c.most_common():
        if cnt == 2:
            l2.append(i)
        elif cnt == 1:
            l1.append(i)
    s = 0
    for i in range(len(l1) + 1):
        for invs in itertools.combinations(l1, i):
            l = [0]
            for k in l1:
