    for di in d {
        *hs.entry(di).or_insert(0) += 1;
    }

    let mut d0 = vec![];
    let mut d1 = vec![];
    for (k,v) in hs {
        if k == 0 {
            if v >= 2 {
                println!("0");
                return
            } else {
                d1.push(0);
            }
        } else if k == 12 {
            if v >= 2 {
                println!("0");
                return
            } else {
                d1.push(12);
            }
        } else {
            if v >= 3 {
                println!("0");
                return
            } else if v == 2 {
                d1.push(k);
                d1.push(24-k);
            } else {
                d0.push(k);
            }
        }
    }

    d0.sort();

    let mut maxv = 0;
    let nd0 = d0.len();
    let nd1 = d1.len();
    for bit in 0..(1<<nd0) {
