                t.push(d0[i]);
            } else {
                t.push(24-d0[i]);
            }
        }
        for i in 0..nd1 {
            t.push(d1[i]);
        }
        let nt = t.len();
        let mut minv = 1<<30;
        for i in 0..nt {
            for j in i+1..nt {
                let ma = max(t[i],t[j]);
                let mi = min(t[i],t[j]);
                let v = min(ma-mi, 24-(ma-mi));
                chmin!(minv, v);
            }
        }
        chmax!(maxv, minv);
    }

    println!("{}", maxv);
}

