            d.push(i as i32);
        }
    }

    let mut max = 0;
    for mask in 0..(1 << d.len()) {
        let mut v = Vec::new();
        for i in 0..d.len() {
            if ((1 << i) & mask) != 0 {
                v.push(d[i]);
            } else {
                v.push(-d[i]);
            }
        }

        for f in &fixed {
            v.push(*f);
            v.push(-*f);
        }

        v.sort();
        let mut min = 12;
        for i in 0..v.len() {
            if i == v.len() - 1 {
                min = cmp::min(min, v[0] + 24 - v[i]);
