const int inf=0x7f7f7f7f;
int n,m,S,T,ans,cnt,tot=1,hd,tl,a[110],b[210],head[210],cur[210],to[20010],nxt[20010],val[20010],pre[20010],dep[210],q[210],num[210];
inline int abs(int x){
	return x>0?x:-x;
}
inline int min(int a,int b){
	return a<b?a:b;
}
inline void add_edge(int u,int v,int w){
	nxt[++tot]=head[u];
	to[tot]=v;
	val[tot]=w;
	head[u]=tot;
	return;
}
inline void Add_edge(int u,int v,int w){
	add_edge(u,v,w);
	add_edge(v,u,0);
	return;
}
void bfs(){
    for(int i=0;i<T;i++)
	    dep[i]=T+1;
    dep[T]=0;
    hd=tl=0;
    q[tl++]=T;
    while(hd<tl){
	    int u=q[hd++];
	    for(register int i=head[u];i;i=nxt[i]){
		    int v=to[i];
		    if(dep[v]==T+1&&val[i^1]){
			    dep[v]=dep[u]+1;
			    q[tl++]=v;
			}
		}
	}
    return;
}
inline int get_flow(){
    int flw=inf;
    for(int u=T;u!=S;u=to[pre[u]^1])
	    flw=min(flw,val[pre[u]]);
    for(int u=T;u!=S;u=to[pre[u]^1]){
	    val[pre[u]]-=flw;
	    val[pre[u]^1]+=flw;
	}
    return flw;
}
void ISAP(){
    int u=S;
    bfs();
    for(int i=0;i<=T;i++){
	    num[dep[i]]++;
	    cur[i]=head[i];
	}
    while(dep[S]<T){
	    if(u==T){
		    ans+=get_flow();
		    u=S;
		}
	    bool ck=0;
	    for(int i=cur[u];i;i=nxt[i])
		    if(dep[to[i]]+1==dep[u]&&val[i]){
			    ck=1;
			    cur[u]=i;
			    pre[u=to[i]]=i;
			    break;
			}
	    if(!ck){
		    int mini=T-1;
		    for(int i=head[u];i;i=nxt[i])
			    if(val[i])
				    mini=min(mini,dep[to[i]]);
		    if(!(--num[dep[u]]))
			    break;
		    num[dep[u]=(mini+1)]++;
		    cur[u]=head[u];
		    if(u!=S)
			    u=to[pre[u]^1];
		}
	}
    return;
}
inline bool check(int x){
	if(x<=2)
		return 0;
	for(register int i=2;i*i<=x;i++){
		if(!(x%i))
			return 0;
	}
	return 1;
}
