			b[++cnt]=a[i-1]+1;
			b[++cnt]=a[i];
		}
	}
	b[++cnt]=a[n]+1;
	S=0,T=cnt+1;
	for(int i=1;i<=cnt;i++){
		if(b[i]&1){
			m++;
			Add_edge(S,i,1);
			for(int j=1;j<=cnt;j++)
				if(!(b[j]&1)&&check(abs(b[i]-b[j])))
					Add_edge(i,j,1);
		}
		else Add_edge(i,T,1);
	}
	ISAP();
	printf("%d",cnt-ans+((m-ans)&1));
	return 0;
}
