	int n; reader(n);
	vector<int> x(n);
	FOR(i, n) reader(x[i]);

	for (int len = 1; len <= n; len++) {
		for (int l = 0; l < n; l++) {
			int r = l + len;
			if (r > n) break;

			int tmp = ten(5);
			for (int md = l + 1; md < r; md++) {
				int ca = dp[l][md] + dp[md][r];
				tmp = min(tmp, ca);
			}

			int cans = f(x[r-1] - x[l] + 1);
			FOR(i, len - 1) {
				int diff = x[l + i + 1] - x[l + i] - 1;
				int ctmp = f(diff);
				cans += ctmp;
			}

			tmp = min(tmp, cans);
			dp[l][r] = tmp;
		}
	}

	int ans = dp[0][n];
	writerLn(ans);

	return 0;
}

