const int INF = 0x3f3f3f3f;
int head[MAXP], nxt[MAXM], to[MAXM], val[MAXM], tot = 1;
void addedge(int b, int e, int v) {
	nxt[++tot] = head[b]; to[head[b] = tot] = e; val[tot] = v;
	nxt[++tot] = head[e]; to[head[e] = tot] = b; val[tot] = 0;
}
std::queue<int> q;
int dis[MAXP];
int S, T, idx;
bool bfs() {
	memset(dis, 0, idx + 1 << 2);
	q.push(S); dis[S] = 1;
	while (!q.empty()) {
		int t = q.front(); q.pop();
		for (int i = head[t]; i; i = nxt[i])
			if (val[i] && !dis[to[i]]) {
				dis[to[i]] = dis[t] + 1;
				q.push(to[i]);
			}
	}
	return dis[T] > 0;
}
int dinic(int u, int minv) {
	if (u == T || !minv) return minv;
	int t, res = 0;
	for (int & i = head[u]; i; i = nxt[i])
		if (val[i] && dis[to[i]] == dis[u] + 1 && (t = dinic(to[i], std::min(minv, val[i])))) {
			val[i] -= t;
			val[i ^ 1] += t;
			res += t;
			minv -= t;
			if (!minv) break;
		}
	if (!res) dis[u] = -1;
	return res;
}
int flow() {
	static int es[MAXP];
	memcpy(es, head, idx + 1 << 2);
	int res = 0;
	while (bfs()) {
		res += dinic(S, INF);
		memcpy(head, es, idx + 1 << 2);
	}
	return res;
}

