void findPrime(int N, vector<bool>& isPrime)
{
    isPrime.assign(N+1, true);
    isPrime[0] = isPrime[1] = false;
    for(int i=2; i*i<=N; i++){
        if(isPrime[i]){
            for(int j=i; i*j<=N; j++){
                isPrime[i*j] = false;
            }
        }
    }
}

const int MAX = 10000000;
const int INF = INT_MAX / 2;

int n;
vector<int> x;
vector<bool> isPrime;
vector<vector<int> > memo;

int getCost(int len)
{
    if(len == 0)
        return 0;
    else if(len >= 3 && isPrime[len])
        return 1;
    else if(len % 2 == 0)
        return 2;
    else
        return 3;
}

int solve(int a, int b)
{
    if(a == n)
        return 0;
    if(memo[a][b] != -1)
        return memo[a][b];

    int len = 1;
    if(b != 0)
        len = x[a] - x[a-1];

    int ans = INT_MAX;
    int sum = 0;
    for(int i=a; i<n; ++i){
        int cost1 = sum + getCost(len) + solve(i+1, 0);
        ans = min(ans, cost1);
        if(i < n - 1){
            len += x[i+1] - x[i] - 1;
            int cost2 = sum + getCost(len) + solve(i+1, 1);
            ans = min(ans, cost2);
            ++ len;
            sum += getCost(x[i+1] - x[i] - 1);
        }
    }

    return memo[a][b] = ans;
}

int main()
{
    findPrime(MAX, isPrime);

    cin >> n;
    x.resize(n);
    for(int i=0; i<n; ++i)
        cin >> x[i];

    memo.assign(n, vector<int>(2, -1));
    cout << solve(0, 0) << endl;

    return 0;
}

