inline int read()
{
	int x=0,w=1; char c=getchar();
	while(c<'0'||c>'9') {if(c=='-') w=-1; c=getchar();}
	while(c<='9'&&c>='0') {x=(x<<1)+(x<<3)+c-'0'; c=getchar();}
	return w==1?x:-x;
}

bool x[N],is[N];
int l[maxn],r[maxn],c[maxn],cr,cl,cnt,pri[maxn],T[maxn],ct;

struct node{int from,to,cap,flow,cost;};
vector <node> edges;
vector <int> mp[maxn];
int s,t,n,m,d[maxn],flow,cost,p[maxn],a[maxn];
bool inq[maxn];
queue <int> que;

inline void A(int u,int v,int w,int c)
{
	edges.push_back((node){u,v,w,0,c});
	edges.push_back((node){v,u,0,0,-c});
	int pp=edges.size();
	mp[u].push_back(pp-2);
	mp[v].push_back(pp-1);
}

inline bool spfa()
{
	for(int i=0;i<=t;i++) d[i]=inf,inq[i]=0,a[i]=inf;
	que.push(s); d[s]=0;
	while(!que.empty())
	{
		int u=que.front(); que.pop(); inq[u]=0;
		for(int i=0;i<mp[u].size();i++)
		{
			node &v=edges[mp[u][i]];
			if(v.cap-v.flow>0&&d[v.to]>d[u]+v.cost)
			{
				d[v.to]=d[u]+v.cost;
				a[v.to]=min(a[u],v.cap-v.flow);
				p[v.to]=mp[u][i];
				if(inq[v.to]==0) que.push(v.to),inq[v.to]=1;
			}
		}
	}
	if(d[t]==inf) return false;
	flow+=a[t];
	cost+=a[t]*d[t];
	for(int i=t;i!=s;i=edges[p[i]].from)
	{
		edges[p[i]].flow+=a[t];
		edges[p[i]^1].flow-=a[t];
	}
	return true;
}

inline void pre()
{
	for(int i=2;i<=(N-4);i++)
	{
		if(!is[i]) pri[++cnt]=i;
		for(int j=1;j<=cnt,pri[j]*i<=(N-5);j++)
		{
			is[pri[j]*i]=1;
			if(i%pri[j]==0) break;
		}
	}
}

int main()
{
	//freopen("t1.out","w",stdout);
	n=read(); pre(); is[1]=1;
	rep(i,1,n) c[i]=read(),x[c[i]]=1;
	rep(i,1,(N-4)) if(x[i]!=x[i-1]) T[++ct]=i;
	//rep(i,1,ct) cout<<T[i]<<" "; cout<<endl;
	s=0,t=2*ct+1;
	rep(i,1,ct) A(s,i,1,0),A(i+ct,t,1,0);
	rep(i,1,ct) rep(j,1,ct)
	{
		if(i==j) continue;
		int tmp=abs(T[i]-T[j]);
		if(tmp%2==0) A(i,j+ct,1,2);
		else if(!is[tmp]) A(i,j+ct,1,1);
		else A(i,j+ct,1,3);
	}
	while(spfa()); cout<<cost/2<<endl;
	return 0;
}

