  vec b;
  ITR(itr,mp) if(itr->sc) b.push_back(itr->fs);

  int m = b.size();

  max_flow mf(m+2);
  REP(i,m) REP(j,m){
    if(b[i]%2) continue;
    if(pri[abs(b[i]-b[j])]) mf.add_edge(i,j,1);
  }

  int e = 0, o = 0;
  REP(i,m){
    if(b[i]%2) mf.add_edge(i,m+1,INF), o++;
    else mf.add_edge(m,i,INF), e++;
  }

  int mx = mf.run(m,m+1);
  cout << mx + (o/2+e/2)*2 + o%2*3 << endl;

  return 0;
}

