bool dfs(int x) {
	if (bio[x])
		return false;
	bio[x] = true;
	for (auto it : E[x])
		if (conn[it] == -1 || dfs(conn[it])) {
			conn[it] = x;
			return true;
		}
	return false;
}

bool prime(int x) {
	if (x == 1)
		return false;
	for (int i = 2; i * i <= x; i++)
		if (!(x % i))
			return false;
	return true;
}

int solve() {
	memset(conn, -1, sizeof conn);
	for (int i = MAXM - 1; i; i--) {
		a[i] ^= a[i - 1];
		if (a[i])
			v[i % 2].push_back(i);
	}

	for (int i = 0; i < v[0].size(); i++)
		for (int j = 0; j < v[1].size(); j++)
			if (prime(abs(v[0][i] - v[1][j]))) 
				E[i].push_back(j);

	int match = 0;
	for (int i = 0; i < v[0].size(); i++) {
		memset(bio, false, sizeof bio);
		match += dfs(i);
	}

	return v[0].size() + v[1].size() - match + (v[0].size() - match) % 2;
}

