struct Dinic{
    struct edge {
        int64_t to, cap, rev;
        edge(int64_t to, int64_t cap, int64_t rev):to(to), cap(cap), rev(rev){}
    };

    int N, S, T;
    int64_t flow = 0;
    const int64_t INF = 1e18;
    vector<int> level, iter;
    vector<vector<edge>> G;

    Dinic(int N, int S, int T):N(N), S(S), T(T){
        flow = 0;
        level.resize(N);
        iter.resize(N);
        G.resize(N);
    }

    void add_edge(int from, int to, int64_t cap){
        G[from].emplace_back(to, cap, G[to].size());
        G[to].emplace_back(from, 0, G[from].size()-1);
    }

    void bfs(int s){
        fill(level.begin(), level.end(), -1);
        queue<int> que;
        level[s] = 0;
        que.push(s);
        while(que.size()){
            int v = que.front(); que.pop();
            for(auto& e : G[v]){
                if(e.cap > 0 && level[e.to] < 0){
                    level[e.to] = level[v] + 1;
                    que.push(e.to);
                }
            }
        }
    }

    int64_t dfs(int v, int t, int64_t f){
        if(v == t) return f;
        for(int& i=iter[v]; i<(int)G[v].size(); i++){
            auto& e = G[v][i];
            if(e.cap > 0 && level[v] < level[e.to]){
                int64_t d = dfs(e.to, t, min(f, e.cap));
                if(d > 0){
                    e.cap -= d;
                    G[e.to][e.rev].cap += d;
                    return d;
                }
            }
        }
        return 0;
    }

    int64_t max_flow(){
        while(true){
            bfs(S);
            if(level[T] < 0) return flow;
            fill(iter.begin(), iter.end(), 0);
            while(true){
                int64_t f = dfs(S, T, INF);
                if(f == 0) break;
                flow += f;
            }
        }
    }
};

bool is_prime(int64_t n){
    if(n <= 1) return false;
    for(int64_t i=2; i*i<=n; i++) if(n % i == 0) return false;
    return true;
}

int main(){
    int N;
    cin >> N;
    map<int, int> diff;
    for(int i=0; i<N; i++){
        int x;
        cin >> x;
        diff[x-1]++;
        diff[x]++;
    }

    vector<int> A[2];
    for(auto& p : diff) if(p.second%2) A[p.first%2].push_back(p.first);
    int sz[2];
    for(int k=0; k<2; k++) sz[k] = A[k].size();

    int S = sz[0]+sz[1];
    int T = S+1;
    Dinic solver(T+1, S, T);
    for(int i=0; i<sz[0]; i++) solver.add_edge(S, i, 1);
    for(int i=0; i<sz[1]; i++) solver.add_edge(sz[0]+i, T, 1);
    for(int i=0; i<sz[0]; i++) for(int j=0; j<sz[1]; j++){
        int d = abs(A[0][i] - A[1][j]);
        if(d%2 && is_prime(d)) solver.add_edge(i, sz[0]+j, 1);
    }

    int m = solver.max_flow();
    int r0 = sz[0]-m, r1 = sz[1]-m;
    int ans = m + r0/2*2 + r1/2*2 + (r0%2)*3;
    cout << ans << endl;
    return 0;
}

