const int maxv = 10000050;
bool np[maxv]; int prm[maxv];
inline void sieve() {
  const int n = 10000005;
  int cnt = 0;
  np[1] = true;
  for(int i = 2; i <= n; i ++) {
    if(!np[i]) {
      prm[cnt ++] = i;
    }
    for(int j = 0; j < cnt; j ++) {
      int v = i * prm[j];
      if(v > n) break;
      np[v] = true;
      if(i % prm[j] == 0) break;
    }
  }
}

const int maxn = 305;
int e_siz, o_siz;
bool G[maxn][maxn];
int cp[maxn]; bool vis[maxn];
bool dfs(int x) {
  for(int i = 1; i <= o_siz; i ++) {
    if(G[x][i] && !vis[i]) {
      vis[i] = true;
      if(!cp[i] || dfs(cp[i])) {
        cp[i] = x; return true;
      }
    }
  }
  return false;
}
inline int hungray() {
  int ans = 0;
  for(int i = 1; i <= e_siz; i ++) {
    memset(vis, 0, sizeof(vis));
    if(dfs(i)) ans ++;
  }
  return ans;
}

int C[maxv];
void build_graph() {
  std::vector<int> ep, op;
  for(int i = 1; i < maxv; i ++) {
    if(C[i]) {
      if(i & 1) op.push_back(i);
      else ep.push_back(i);
    }
  }
  e_siz = ep.size(); o_siz = op.size();
  for(int i = 1; i <= e_siz; i ++) {
    for(int j = 1; j <= o_siz; j ++) {
      int dis = ep[i - 1] - op[j - 1];
      if(dis < 0) dis *= -1;
      if(!np[dis]) {
        G[i][j] = true;
      }
    }
  }
}

int main() {
  sieve();
  int n; scanf("%d", &n);
  for(int i = 1; i <= n; i ++) {
    int x; scanf("%d", &x);
    C[x] ^= 1; C[x + 1] ^= 1;
  }
  build_graph();
  int ans = 0;
  int v_1 = hungray(); ans += v_1;
  int e_cnt = e_siz, o_cnt = o_siz;
  if(v_1 < e_cnt || v_1 < o_cnt) {
    e_cnt -= v_1; o_cnt -= v_1;
    ans += (e_cnt >> 1) << 1; e_cnt &= 1;
    ans += (o_cnt >> 1) << 1; o_cnt &= 1;
    if(e_cnt) ans += 3;
  }
  printf("%d\n", ans);
  return 0;
}

