#include <bits/stdc++.h>
using namespace std;
typedef long long ll;

const int INF = (int)1e9;

namespace math {
    const int N = (int)1.01e7;

    char ispr[N];
    vector<int> pr;

    void init() {
        memset(ispr, 1, sizeof(ispr));
        ispr[1] = 0;
        for (int i = 2; i < N; i++) {
            if (!ispr[i]) continue;
            pr.push_back(i);
            for (int j = i + i; j < N; j += i) ispr[j] = 0;
        }
    }

    bool isPrime(int x) {
        if (x < N) return ispr[x];
        for (int i = 0; i < (int)pr.size() && 1LL * pr[i] * pr[i] <= x; i++) {
            if (x % pr[i] == 0) {
                return 0;
            }
        }
        return 1;
    }
}

