}

int fastsolve(vector<int> x) {
    int n = x.size();

    vector<int> vv;
    vv.push_back(x[0]);
    for (int i = 0; i + 1 < n; i++) {
        if (x[i + 1] != x[i] + 1) {
            vv.push_back(x[i] + 1);
            vv.push_back(x[i + 1]);
        }
    }
    vv.push_back(x.back() + 1);

    vector<vector<int> > vct(2);
    for (int x : vv) {
        vct[x % 2].push_back(x);
    }
    vector<vector<int> > e(vct[0].size());
    for (int i = 0; i < (int)vct[0].size(); i++) {
        for (int j = 0; j < (int)vct[1].size(); j++) {
            if (isPrime(abs(vct[0][i] - vct[1][j]))) {
                e[i].push_back(j);
            }
        }
    }


    vector<char> vis;
    vector<int> mt(vct[1].size(), -1);
    function<bool(int)> dfs = [&](int v) {
        if (vis[v]) return false;
        vis[v] = 1;
        for (int to : e[v]) {
            if (mt[to] == -1 || dfs(mt[to])) {
                mt[to] = v;
                return true;
            }
        }
        return false;
    };
    int mat = 0;
    for (int i = 0; i < (int)vct[0].size(); i++) {
        vis.assign(vct[0].size(), 0);
        mat += dfs(i);
    }
    int c0 = vct[0].size() - mat;
    int c1 = vct[1].size() - mat;

    int ans = 0;
    ans += mat;
    ans += c0 / 2 * 2;
    ans += c1 / 2 * 2;
    ans += c0 % 2 * (c1 % 2) * 3;

    return ans;
}

