vector<int> pr, pchk;
void getPrime() {
    pchk = vector<int>(10000010, 0);
    for(int i = 2; i < 10000010; i++) {
        if(pchk[i]) continue;
        int mul = 2;
        while(i * mul < 10000010) {
            pchk[i * mul] = 1;
            mul++;
        }
    }
    pchk[2] = 1;
}

int N;
vector<int> X;

vector<vector<int> > cc;
int dp(int l, int r) {
    int &ret = cc[l][r];
    if(ret != -1) return ret;


    int pre = X[l];
    int tmp = 0;
    for(int i = l + 1; i <= r + 1; i++) {
        if(i == r + 1 || X[i - 1] + 1 != X[i]) {
            tmp += (pchk[ X[i - 1] - pre + 1 ]? (((X[i - 1] - pre + 1) % 2)? 3 : 2) : 1);
            if(i != r + 1) pre = X[i];
        }
    }
    ret = tmp;
    pre = 1;
    tmp = 0;
    for(int i = l; i <= r; i++) {
        tmp += (X[i] - pre) % 2 == 0? (X[i] - pre == 0? 0 : 2) : (pchk[ X[i] - pre ]? 3 : 1);
        pre = X[i] + 1;
    }
    if(pchk[ X[r] ]) tmp += (X[r] % 2 == 0? 2 : 3);
    else tmp++;

    ret = min(ret, tmp);
    for(int i = l; i < r; i++) {
        ret = min(ret, dp(l, i) + dp(i + 1, r));
    }
    return ret;
}

