int n,a[M],prime[M],sum,cnt1,cnt2,od[N],ev[N];
bool pr[M]; queue<int> Q; int dis[N],cur[N];
int fi[N],ne[N<<4],to[N<<4],c[N<<4],S,T,V,E;
bool bfs(int S,int T){
	while(!Q.empty())Q.pop(); Q.push(S);
	for(int i=1;i<=V;i++)dis[i]=inf,cur[i]=fi[i]; dis[S]=0;
	while(!Q.empty()){
		int u=Q.front();Q.pop();
		for(int i=fi[u];i;i=ne[i])
		if(c[i]&&dis[to[i]]==inf){
			int v=to[i];
			dis[v]=dis[u]+1;
			if(v==T)return 1; Q.push(v);
		}
	}
	return dis[T]<=V;
}
int dfs(int u,int T,int flow){
	if(u==T)return flow;
	int usd=0;
	for(int i=cur[u];i;i=cur[u]=ne[i])
	if(c[i]&&dis[to[i]]==dis[u]+1){
		int fl=dfs(to[i],T,min(flow-usd,c[i]));
		if(!fl)continue;c[i]-=fl,c[i^1]+=fl,usd+=fl;
		if(usd>=flow)return usd;
	}
	return usd;
}
int dinic(int S,int T){
	int ans=0;
	while(bfs(S,T))ans+=dfs(S,T,inf);
	return ans;	
}
void add(int x,int y,int z){
	ne[++E]=fi[x],fi[x]=E,to[E]=y,c[E]=z;
	ne[++E]=fi[y],fi[y]=E,to[E]=x,c[E]=z;
}
void Eular(int n){
	pr[1]=1;
	for(int i=2;i<=n;i++){
		if(!pr[i]){
			prime[++sum]=i;
			if(i==2)pr[i]=1;
		}
		for(int j=1;j<=sum;j++){
			if(prime[j]*i>n)break;
			pr[prime[j]*i]=1;if(i%prime[j]==0)break;
		}
	}
}
