#define ll long long

bool isPrime(int p) {
	if (p == 1) return false;
	for (ll i = 2; i * i <= p; ++i) {
		if (p % i == 0)
			return false;
	}
	return true;
}

struct BipartiteMatching {
	vector< vector< int > > graph;
	vector< int > match, alive, used;
	int timestamp;

	BipartiteMatching(int n) : graph(n), alive(n, 1), used(n, 0), match(n, -1), timestamp(0) {}

	void add_edge(int u, int v) {
		graph[u].push_back(v);
		graph[v].push_back(u);
	}

	bool dfs(int idx) {
		used[idx] = timestamp;
		for (auto& to : graph[idx]) {
			int to_match = match[to];
			if (alive[to] == 0) continue;
			if (to_match == -1 || (used[to_match] != timestamp && dfs(to_match))) {
				match[idx] = to;
				match[to] = idx;
				return true;
			}
		}
		return false;
	}

	int bipartite_matching() {
		int ret = 0;
		for (int i = 0; i < graph.size(); i++) {
			if (alive[i] == 0) continue;
			if (match[i] == -1) {
				++timestamp;
				ret += dfs(i);
			}
		}
		return ret;
	}
};

int main() {
	int N; cin >> N;
	vector<int> x(N);
	for (int i = 0; i < N; ++i)
		cin >> x[i];
	vector<int> idx_even, idx_odd;
	for (int i = 0; i < N; ++i) {
		if (i == 0 || x[i - 1] + 1 != x[i]) {
			if (x[i] % 2) idx_even.push_back(x[i] - 1);
			else idx_odd.push_back(x[i] - 1);
		}
		if (i == N - 1 || x[i] + 1 != x[i + 1]) {
			if (x[i] % 2) idx_odd.push_back(x[i]);
			else idx_even.push_back(x[i]);
		}
	}

	/*
	for (auto i : idx_even) cout << i << " ";
	cout << endl;
	for (auto i : idx_odd) cout << i << " ";
	cout << endl;
	*/

	int sz = idx_even.size() + idx_odd.size();
	BipartiteMatching bm(sz);
	for (int i = 0; i < idx_even.size(); ++i) {
		for (int j = 0; j < idx_odd.size(); ++j) {
			int d = abs(idx_even[i] - idx_odd[j]);
			if (d != 2 && isPrime(d)) {
				int id1 = i, id2 = idx_even.size() + j;
				bm.add_edge(id1, id2);
			}
		}
	}

	int res = bm.bipartite_matching();
	int left_even = idx_even.size() - res;
	int left_odd = idx_odd.size() - res;
	res += left_even / 2 * 2;
	res += left_odd / 2 * 2;
	if (left_even % 2) {
		res += 3;
	}

	cout << res << endl;

	return 0;
}

