using namespace std;

const int N = 110;
const int M = 1e7 + 10;

bool vis[M];
int prm[M], tot;

void init(int n){
	vis[1] = true;
	For(i, 2, n){
		if(!vis[i]) prm[++tot] = i;
		for(int j = 1; j <= tot && prm[j] * i <= n; ++j){
			vis[prm[j] * i] = true;
			if(i % prm[j] == 0) break;
		}
	}
}

int n, m;
vector<int> G[N];
int pos[N << 1], match[N], used[N];

bool Hungary(int o){
	if(used[o]) return false;
	used[o] = true;
	for(int v : G[o]){
		int &x = match[v];
		if(!x || Hungary(x)){
			x = o;
			return true;
		}
	}
	return false;
}

int main(){

	init(M - 5);
	scanf("%d", &n);
	For(i, 1, n){
		int x;
		scanf("%d", &x);
		pos[++m] = x, pos[++m] = x + 1;
	}

	sort(pos + 1, pos + m + 1);
	int node = 0;
	For(i, 1, m){
		if(pos[i + 1] == pos[i]) ++i;
		else pos[++node] = pos[i];
	}
	For(i, 1, node) if(pos[i] & 1)
		For(j, 1, node){
			int x = abs(pos[i] - pos[j]);
			if(pos[j] % 2 == 0 && !vis[x]){
				G[i].pb(j);
			}
		}

	int ret = 0, odd = 0;
	For(i, 1, node) if(pos[i] & 1){
		++odd;
		memset(used, false, sizeof used);
		ret += Hungary(i);
	}

	int even = node - odd - ret;
	odd -= ret;
	if(even & 1) ret = node - ret + 1;
	else ret = node - ret;

	printf("%d\n", ret);

	return 0;
}

