using namespace Factorize;
struct HopcroftKarp {
    vector<vector<int>> graph;
    vector<int> dist, match;
    vector<bool> used, vv;

    HopcroftKarp(int n, int m) : graph(n), match(m, -1), used(n) {}

    void add_edge(int u, int v) { graph[u].push_back(v); }

    void bfs() {
        dist.assign(graph.size(), -1);
        queue<int> que;
        for(int i = 0; i < graph.size(); i++) {
            if(!used[i]) {
                que.emplace(i);
                dist[i] = 0;
            }
        }

        while(!que.empty()) {
            int a = que.front();
            que.pop();
            for(auto &b : graph[a]) {
                int c = match[b];
                if(c >= 0 && dist[c] == -1) {
                    dist[c] = dist[a] + 1;
                    que.emplace(c);
                }
            }
        }
    }

    bool dfs(int a) {
        vv[a] = true;
        for(auto &b : graph[a]) {
            int c = match[b];
            if(c < 0 || (!vv[c] && dist[c] == dist[a] + 1 && dfs(c))) {
                match[b] = a;
                used[a] = true;
                return (true);
            }
        }
        return (false);
    }

    int bipartite_matching() {
        int ret = 0;
        while(true) {
            bfs();
            vv.assign(graph.size(), false);
            int flow = 0;
            for(int i = 0; i < graph.size(); i++) {
                if(!used[i] && dfs(i)) ++flow;
            }
            if(flow == 0) return (ret);
            ret += flow;
        }
    }

    void output() {
        for(int i = 0; i < match.size(); i++) {
            if(~match[i]) { cout << match[i] << "-" << i << endl; }
        }
    }
};

