    const int N = 1e7 + 2;
    vi p(N);
    rep(i, n) p[a[i]]++;
    rep(i, N - 1) { p[i] = p[i + 1] ^ p[i]; }
    vi x[2];
    rep(i, N) {
        if(p[i]) x[i & 1].eb(i);
    }
    HopcroftKarp g(x[0].size(), x[1].size());
    rep(i, x[0].size()) {
        rep(j, x[1].size()) {
            int d = abs(x[1][j] - x[0][i]);
            if(isPrime(d)) g.add_edge(i, j);
        }
    }
    int d = g.bipartite_matching();
    int ans = x[0].size() + x[1].size() + d;
    if((x[0].size() - d) & 1) ans++;
    cout << ans << endl;
}

