struct Ford_Fulkerson {

	struct f_edge {
		ll to;
		ll cap;
		ll fl;
		ll rev;//
	};


	ll N;
	ll s, t;
	vector<vector<edge>> g;//flow
	vector<vector<f_edge>> flow;
	ll maxflow;

	vector<bool> see;
	ll dfs(ll v, ll x) {
		see[v] = 1;

		if (v == t) { return x; }

		rep(i, flow[v].size()) {
			const auto e = flow[v][i];

			if (see[e.to] == 1) { continue; }
			if (e.cap == e.fl) { continue; }

			ll y = min(x, e.cap - e.fl);

			y = dfs(e.to, y);
			if (y == 0) { continue; }

			flow[v][i].fl += y;
			flow[e.to][e.rev].fl -= y;
			return y;
		}

		return 0;
	}

	Ford_Fulkerson(ll N0, vector<vector<edge>> g0, ll s0, ll t0) {
		N = N0;
		g = g0;
		s = s0;
		t = t0;

		flow.resize(N + 1);
		repn(v, N) {
			for (edge e : g[v]) {
				ll w = e.to;
				flow[v].push_back({ w,e.d,0,flow[w].size() });
				flow[w].push_back({ v,0,0,flow[v].size() - 1 });
			}
		}

		maxflow = 0;

		if (s == t) { maxflow = INF; return; }

		while (1) {
			see.assign(N + 1, 0);
			ll f = dfs(s, INF);
			if (f == 0) { break; }
			maxflow += f;
		}

		maxflow = min(INF, maxflow);

		return;

	}
};

bool odprime(ll x) {
	if (x <= 2) { return 0; }
	for (ll i = 2; i * i <= x; i++) {
		if (x % i == 0) { return 0; }
	}

	return 1;
}

