bool go(vector<vector<int> > &g, int u, vector<int> &mt, vector<bool> &vs){
	int n = g.size();
	if(u<0) return true;
	for(int v = 0; v < n; v++){
		if(!g[u][v] || vs[v] || mt[v]!=-1) continue;
		vs[v] = true;
		mt[v] = u;
		return true;
	}
	for(int v = 0; v < n; v++){
		if(!g[u][v] || vs[v]) continue;
		vs[v] = true;
		if(go(g, mt[v], mt, vs)){
			mt[v] = u;
			return true;
		}
	}
	return false;
}
int bm(vector<vector<int> > &g){
	int n = g.size();
	vector<int> mt(n, -1);
	int res = 0;
	for(int i = 0; i < n; i++){
		vector<bool> vs(n);
		if(go(g, i, mt, vs)) res++;
	}
	return res;
}

bool isPrime3(ll x){
	if(x<=2) return false;
	for(ll i = 2; i*i<=x; i++) if(x%i==0) return false;
	return true;
}

int main(){
	int n;
	cin>>n;
	vector<ll> a;
	rep(i, n){
		ll x;
		cin>>x;
		if(a.empty() || a.back()!=x){
			a.push_back(x);
			a.push_back(0);
		}
		a.back() = x+1;
	}
	ll m = a.size();
	vector<vector<int> > g(m, vector<int>(m));
	rep(i, m){
		rep(j, i){
			if(isPrime3(abs(a[i]-a[j]))){
				if(a[i]%2) g[i][j] = 1;
				else g[j][i] = 1;
			}
		}
	}
	ll t = bm(g);
	ll odd = 0;
	rep(i, m) if(a[i]%2) odd++;
	ll s = (odd-t)%2;
	ll res = t + s*3 + (m/2-s-t)*2;
	cout<<res<<endl;
	return 0;
}

