const int maxn=210;
int n,k,pri[10000010],linker[maxn],a[maxn],b[maxn];
bool mark[10000010],vis[maxn];
vector<int> v[maxn];
void pre()
{
	n=10000000;
	mark[1]=true;
	for (int i=2;i<=n;i++)
	{
		if (!mark[i])
		{
			pri[k++]=i;
		}
		for (int j=0;j<k;j++)
		{
			int m=i*pri[j];
			if (m>n)
			{
				break;
			}
			mark[m]=true;
			if (i%pri[j]==0)
			{
				break;
			}
		}
	}
}
bool dfs(int x)
{
	for (int i=0;i<v[x].size();i++)
	{
		int y=v[x][i];
		if (vis[y])
		{
			continue;
		}
		vis[y]=true;
		if (linker[y]==-1 || dfs(linker[y]))
		{
			linker[y]=x;
			return true;
		}
	}
	return false;
}
int main()
{
	pre();
	scanf("%d",&n);
	for (int i=1;i<=n;i++)
	{
		scanf("%d",&a[i]);
	}
	int k=0;
	b[++k]=a[1]-1;
	for (int i=2;i<=n;i++)
	{
		if (a[i]!=a[i-1]+1)
		{
			b[++k]=a[i-1];
			b[++k]=a[i]-1;
		}
	}
	b[++k]=a[n];
	n=k;
	for (int i=1;i<=n;i++)
	{
		linker[i]=-1;
		for (int j=i+1;j<=n;j++)
		{
			if ((b[i]+b[j])&1 && !mark[abs(b[i]-b[j])])
			{
				if (b[i]&1)
				{
					v[i].pb(j);
				}
				else
				{
					v[j].pb(i);
				}
			}
		}
	}
	int s=0;
	for (int i=1;i<=n;i++)
	{
		if (!(b[i]&1))
		{
			continue;
		}
		memset(vis,false,sizeof(vis));
		if (dfs(i))
		{
			s++;
		}
	}
	int s1=0,s2=0;
	for (int i=1;i<=n;i++)
	{
		if (b[i]&1)
		{
			s1++;
		}
		else
		{
			s2++;
		}
	}
	int ans=s+((s1-s)/2+(s2-s)/2)*2+((s1-s)&1)*3;
	cout<<ans<<endl;
	return 0;
}

