int t,n,x[N],st[N],dep[N],top,size,head[N],S,T,pre[10000020],cnt;
bool isp[10000020];
struct ndoe{
	int next,to,vlu;
}mem[N*N*4];
void add(int u,int v,int vlu)
{
	mem[++size].to=v;mem[size].next=head[u];head[u]=size;mem[size].vlu=vlu;
	mem[++size].to=u;mem[size].next=head[v];head[v]=size;mem[size].vlu=0;
}
void init()
{
	int top=1e7;
	isp[1]=1;
	for(int i=2;i<=top;++i)
	{
		if(!isp[i]) pre[++cnt]=i;
		for(int j=1;j<=cnt&&pre[j]*i<=top;++j)
		{
			isp[pre[j]*i]=1;
			if(i%pre[j]==0) break;
		}
	}
}
queue<int>q;
bool bfs(){
	memset(dep,0,sizeof(dep));
	dep[S]=1;
	q.push(S);
	while(!q.empty())
	{
		int x=q.front();q.pop();
		for(int i=head[x];i;i=mem[i].next)
		{
			int v=mem[i].to;
			if(mem[i].vlu&&!dep[v])
			{
				dep[v]=dep[x]+1;
				q.push(v);
			}
		}
	}
	return dep[T];
}
int dfs(int x,int flow)
{
	if(x==T) return flow;
	int now,used=0;
	for(int i=head[x];i;i=mem[i].next)
	{
		int v=mem[i].to;
		if(mem[i].vlu&&dep[v]==dep[x]+1)
		{
			now=dfs(v,min(mem[i].vlu,flow-used));
			used+=now;
			mem[i].vlu-=now;
			mem[i^1].vlu+=now;
			if(used==flow) break;
		}
	}
	if(used!=flow) dep[x]=-1;
	return used;
}
int main()
{
