		for(int i=1;i<=n;++i) x[i]=read();
		for(int i=1;i<=n;++i)
		{
			st[++top]=x[i]-1;
			int now=x[i],r=i;
			while(x[r]==now&&r<=n) now++,r++;
			i=r-1;
			st[++top]=x[r-1];
		}
	//	for(int i=1;i<=top;++i) cout<<st[i]<<" ";cout<<'\n';
		S=top+1,T=S+1;
		int s1=0,s2=0;
		for(int i=1;i<=top;++i)
		{
			if(st[i]&1) add(S,i,1),s1++;
			else add(i,T,1),s2++;
			for(int j=i+1;j<=top;++j) if(st[j]-st[i]!=2&&!isp[st[j]-st[i]])
			if(st[i]&1) add(i,j,1);
			else add(j,i,1);
		}
		int k=0;

		while(bfs()) k+=dfs(S,1e9);
	//	cout<<"k"<<k<<" "<<s1<<" "<<s2<<'\n';
		cout<<k+2*((s1-k)/2+(s2-k)/2)+3*((s1-k)%2)<<'\n';
	}
	return 0;
}
