const int N=1e7;
const int maxn=N+10;

int n,m,m0,m1,c[210],k1,left[210];
bool vis[210];
bool comp[maxn]; //composite
int primes,prime[maxn/10];

void prework()
{
	int i,j;
	comp[0]=1;
	comp[1]=1;
	for (i=2;i<=N;i++)
	{
		if (!comp[i]) prime[++primes]=i;
		for (j=1;j<=primes&&i*prime[j]<=N;j++)
		{
			comp[i*prime[j]]=1;
			if (i%prime[j]==0) break;
		}
	}
	comp[2]=1;
}

bool match(int u)
{
	for (int i=1;i<=m;i++)
	{
		int v=c[i];
		if (comp[abs(u-v)]||vis[v]) continue;
		vis[v]=1;
		if (!left[v]||match(left[v]))
		{
			left[v]=u;
			return 1;
		}
	}
	return 0;
}

