		c[i<<1]=x+1;
	}
	sort(c+1,c+n+n+1);
	for (i=1;i<=n+n;i++)
		c[i]==c[m]?m--:c[++m]=c[i];
	for (i=1;i<=m;i++)
		c[i]&1?m1++:m0++;
	for (i=1;i<=m;i++)
	{
		memset(vis,0,sizeof(vis));
		if (c[i]&1) k1+=match(i);
	}
	m0-=k1; m1-=k1;
	printf("%d\n",k1+((m0>>1)+(m1>>1))*2+(m0&1)*3);
}

