const int N=110;
bool pd(int k){
	if(k==1) return 0;
	int sk=sqrt(k);
	for(int i=2;i<=sk;i++) if(!(k%i)) return 0;
	return 1;
}
int n,b[N<<1],link[N],e[N<<1],o[N<<1];
bool g[N][N],vis[N];
bool dfs(int k){
	for(int i=1;i<=e[0];i++){
		if(g[k][i]&&!vis[i]){
			vis[i]=1;
			if(link[i]==-1||dfs(link[i])){
				link[i]=k;
				return 1;
			}
		}
	}
	return 0;
}
