	}
	for(int i=1;i<=b[0];i++){
		if(b[i]&1) o[++o[0]]=b[i];
		else e[++e[0]]=b[i];
	}
	for(int i=1;i<=o[0];i++)
		for(int j=1;j<=e[0];j++){
			if(pd(abs(o[i]-e[j]))) g[i][j]=1;
		}
	int k=0;
	memset(link,-1,sizeof(link));
