bool BFS()
{
    memset(vis,0,sizeof(vis));
    queue<int> q;
    q.push(s);
    d[s]=0;vis[s]=1;
    while(!q.empty())
    {
        int u=q.front();
        for(int tmp=h[u];~tmp;tmp=e[tmp].next)
            if(e[tmp].capa>e[tmp].flow&&!vis[e[tmp].to])
            {
                vis[e[tmp].to]=1;
                d[e[tmp].to]=d[u]+1;
                q.push(e[tmp].to);
            }
        q.pop();
    }
    return vis[t];
}

int DFS(int u,int a)
{
    if(u==t||a==0) return a;
    int f,flow=0;
    for(int& tmp=cur[u];~tmp;tmp=e[tmp].next)
        if(d[e[tmp].to]==d[u]+1)
        {
            f=DFS(e[tmp].to,min(a,e[tmp].capa-e[tmp].flow));
            if(f>0)
            {
                flow+=f;a-=f;
                e[tmp].flow+=f;
                e[tmp^1].flow-=f;
                if(a==0) break;
            }
        }
    return flow;
}

int Dinic()
{
