  if(come[s])return 0;
  come[s] = true;
  if(s == t)return f;
  for(int i = 0;i < edge[s].size();i++){
    E &e = edge[s][i];
    if(e.lim == 0)continue;
    int tmp = flow(e.to, t, min(f, e.lim));
    if(tmp == 0)continue;
    e.lim -= tmp;
    edge[e.to][e.inv].lim += tmp;
    return tmp;
  }
  return 0;
}

int max_flow(int s, int t){
  int res = 0;
  while(true){
    for(int i = 0;i < 300;i++)come[i] = false;
    int tmp = flow(s, t, INF);
    if(tmp == 0)return res;
    res += tmp;
  }
}


