int Q(int x){
	if(x%2==0)return 2;
	return prime::isprime(x) ? 1 : 3;
}
void update(int&x,int y){
	if(x==-1 || x>y)x=y;
}
void dp(int from,int to){
	if(d[from][to][0] != -1)return;
	if(from > to){
		d[from][to][0] = d[from][to][1] = 0;
		return;
	}
	if(from == to){
		d[from][to][col[from]] = 0;
		d[from][to][!col[from]] = Q(sum[to]-sum[from-1]);
	}else{
		for(int i=from+1; i<=to; i++){
			dp(from, i-1);
			dp(i,to);
		}
		for(int c=0; c<2; c++)
		for(int i=from+1; i<=to; i++){
			update(d[from][to][c], d[from][i-1][c] + d[i][to][c]);
		}
	}
	update(d[from][to][0],d[from][to][1] + Q(sum[to]-sum[from-1]));
	update(d[from][to][1],d[from][to][0] + Q(sum[to]-sum[from-1]));
}
