struct HopcroftKarp {
    static const int MAXV = 1e3 + 5;
    static const int MAXE = 1e6 + 5;
    int nx, ny, E, adj[MAXE], nxt[MAXE], lst[MAXV], cur[MAXV], lev[MAXV], que[MAXV], matx[MAXV], maty[MAXV];
    void init(int nx, int ny) {
        this->nx = nx, this->ny = ny;
        E = 0, fill_n(lst, nx, -1);
        fill_n(matx, nx, -1), fill_n(maty, ny, -1);
    }
    void add(int x, int y) {
        adj[E] = y, nxt[E] = lst[x], lst[x] = E++;
    }
    int bfs() {
        int qsize = 0;
        for (int x = 0; x < nx; x++) if (matx[x] != -1) lev[x] = 0;
        else {
            lev[x] = 1;
            que[qsize++] = x;
        }
        int found = 0;
        for (int i = 0; i < qsize; i++) {
            for (int x = que[i], e = lst[x]; ~e; e = nxt[e]) {
                int y = adj[e];
                if (!~maty[y]) found = 1;
                else if (!lev[maty[y]]) {
                    lev[maty[y]] = lev[x] + 1;
                    que[qsize++] = maty[y];
                }
            }
        }
        return found;
    }
    int dfs(int x) {
        for (int& e = cur[x]; ~e; e = nxt[e]) {
            int y = adj[e];
            if (!~maty[y] || (lev[maty[y]] == lev[x] + 1 && dfs(maty[y]))) {
                matx[x] = y;
                maty[y] = x;
                return 1;
            }
        }
        return 0;
    }
    int maxmat() {
        int res = 0;
        while (bfs()) {
            for (int x = 0; x < nx; x++) cur[x] = lst[x];
            for (int x = 0; x < nx; x++) if (!~matx[x]) res += dfs(x);
        }
        return res;
    }
} hopkarp;

const int maxn = 100 + 5;
const int maxx = 1e7 + 100;
vi pr;
int n;
int f[maxx];
int ispr[maxx];

void solve() {
    fill_n(ispr + 2, maxx - 2, 1);
    FOR(i, 2, maxx) if (ispr[i]) {
        for (int j = i + i; j < maxx; j += i) {
            ispr[j] = 0;
        }
    }
    ispr[2] = 0;
    cin >> n;
    FOR(i, 0, n) {
        int x; cin >> x;
        f[x] ^= 1;
        f[x + 1] ^= 1;
    }
    vi ver;
    int odds = 0, evens = 0;
    FOR(i, 1, maxx) {
        if (f[i]) {
            ver.pb(i);
            if (i & 1) {
                odds++;
            }
            else {
                evens++;
            }
        }
    }
    hopkarp.init(sz(ver), sz(ver));
    FOR(i, 0, sz(ver)) FOR(j, 0, sz(ver)) {
        int x = ver[i];
        int y = ver[j];
        if (x % 2 == 0 && y % 2 == 1 && ispr[abs(x - y)]) {
            hopkarp.add(i, j);
        }
    }
    int mx = hopkarp.maxmat();
    int nx = odds - mx;
    int ny = evens - mx;
    int ans = mx;
    ans += nx / 2 * 2;
    ans += ny / 2 * 2;
    if (nx % 2 == 1 && ny % 2 == 1) {
        ans += 3;
    }
    cout << ans << "\n";
}

