const int MAX=1e7+5;
bool isprime[MAX];

void sieve(){
	for(int i=3; i<MAX; i+=2){
		isprime[i]=1;
	}
	isprime[2]=1;
	for(int i=3; i<MAX; i++){
		if(isprime[i]){
