            for (int i=head[u];i;i=e[i].nxt){
                v=e[i].y,s=e[i].s;
                if (dep[v]<0 && s){
                    dep[v]=dep[u]+1;
                    q.push(v);
                }
            }
        }
        if (dep[T]<0){
            break;
        }
        ans+=dfs(S,INF);
    }
    return ans;
}

