const int MAX = 1e7 + 10;

int A[MAX];
int B[MAX];
bool prime[MAX];

struct Graph {
	vvi g;
	vi mt;
	vi used;
	int n, m;

	Graph() = default;

	Graph(int n, int m) : n(n), m(m) {
		g.resize(n);
	}

	void addE(int v, int to) {
		g[v].pb(to);
	} 

	bool dfs(int v) {
		if  (used[v]) {
			return 0;
		}
		for (int to : g[v]) {
			if  (mt[to] == -1 || dfs(mt[to])) {
				mt[to] = v;
				return 1;
			}
		}
		used[v] = 1;
		return 0;
	}

	vii getMatch() {
		mt.assign(m, -1);
		forn(v, n) {
			used.assign(n, 0);
			dfs(v);
		}

		vii res;
		forn(i, m) {
			if  (mt[i] != -1) {
				res.pb(mp(mt[i], i));
			}
		}
		return res;
	}
};

int calc() {
	vi vals;
	forn(i, MAX) {
		if  (B[i]) {
			vals.pb(i);
		}
	}

	sort(all(vals));
	eprintf("vals:\n");
	for (int val : vals) {
		eprintf("%d ", val);
	}
	eprintf("\n");

	assert(sz(vals) % 2 == 0);

	vi odd;
	vi even;
	for (int val : vals) {
		if  (val & 1) {
			odd.pb(val);
		} else {
			even.pb(val);
		}
	}

	Graph g(sz(odd), sz(even));
	forn(i, sz(odd)) {
		forn(j, sz(even)) {
			const int p = abs(odd[i] - even[j]);
			if  (p >= 3 && prime[p]) {
				g.addE(i, j);
			}
		}
	}

	vii match = g.getMatch();
	int ans = sz(match);

	int cnt_even = sz(even) - sz(match);
	int cnt_odd = sz(odd) - sz(match);

	ans += 2 * (cnt_even / 2);
	cnt_even %= 2;

	ans += 2 * (cnt_odd / 2);
	cnt_odd %= 2;

	assert(cnt_odd == cnt_even);
	if  (cnt_even) {
		ans += 3;
	}

	return ans;
}

int solve() {
	memset (prime, true, sizeof prime);
	for (int i = 2; i < MAX; ++i) {
		if  (prime[i]) {
			for (int j = i + i; j < MAX; j += i) {
				prime[j] = 0;
			}
		}
	}

	memset (A, 0, sizeof A);
	for (int x : xs) {
		A[x] ^= 1;
	}

	memset (B, 0, sizeof B);
	forn(i, MAX) {
		B[i] = A[i] ^ A[i + 1];
	}

	int ans = calc();
	return ans;
}

