bool ok[M],vis[N];
vector<int> g[N];
map<int,int> mp;

bool dfs(int u){
	for (int v:g[u])
		if (!vis[v]){
			vis[v]=1;
			if (!pre[v]||dfs(pre[v])){pre[v]=u; return 1;}
		}
	return 0;
}

int main(){
	p=10000001;
	for (int i=2;i<=p;++i){
		if (!ok[i]) pr[++tot]=i;
		for (int j=1;j<=tot;++j){
			if (i*pr[j]>p) break;
			ok[i*pr[j]]=1;
			if (i%pr[j]==0) break;
		}
	}
	ok[1]=ok[2]=1;
	scanf("%d",&n);
	for (int i=1;i<=n;++i){
		int x; scanf("%d",&x);
		mp[x]^=1; mp[x+1]^=1;
	}
	for (auto i:mp){
		if (i.second) a[++m]=i.first;
	}
	for (int i=1;i<=m;++i){
		for (int j=1;j<i;++j)
			if (!ok[a[i]-a[j]]){
				g[i].push_back(j);
				g[j].push_back(i);
			}
	}
	for (int i=1;i<=m;++i){
		memset(vis,0,sizeof vis);
		if (a[i]&1){
			if (dfs(i)) ++ans;
			++num;
		}
	}
	ans+=((num-ans)/2+(m-num-ans)/2)*2+((num-ans)&1)*3;
	printf("%d\n",ans);

	return 0;
}

